          </div>
        </div>

        <div className="map-container">
          <NetworkGraph
            nodes={graphNodes}
            edges={graphEdges}
            highlightedNodes={highlightedNodes}
            onNodeClick={(node) => {
              const crime = mockCrimes.find((c) => c.id === node.id);
