        </div>

        {/* Detective case info banner */}
        {useDetectiveCase && (
          <div className="detective-banner">
            <div className="banner-icon">üöó</div>
            <div className="banner-content">
              <h4>V·ª• √°n: Tr·ªôm c·∫Øp xe Mercedes - Khu Lincoln Park</h4>
              <p>
                M·ªôt v·ª• tr·ªôm c·∫Øp xe sang c√≥ t·ªï ch·ª©c. T∆∞·ªüng ch·ª´ng ƒë∆°n l·∫ª, nh∆∞ng
                H·ªá th·ªëng ph√°t hi·ªán ƒëi·ªÅu kh√°c...
              </p>
            </div>
