        )}
      </div>

      {/* Control panel */}
      <div className="control-panel">
        <div className="panel">
          <div className="panel-header">
            <h2 className="panel-title">B·∫£ng ƒëi·ªÅu khi·ªÉn</h2>
          </div>

          {useDetectiveCase ? (
            <div className="detective-case-card">
              <div className="case-header">
                <span className="case-badge urgent">üö® URGENT</span>
                <span className="case-id">{detectiveStoryCase.id}</span>
              </div>
              <div className="case-details">
                <div className="detail-row">
                  <span className="detail-label">Lo·∫°i:</span>
                  <span className="detail-value">{detectiveStoryCase.type}</span>
                </div>
                <div className="detail-row">
                  <span className="detail-label">Ph∆∞∆°ng ti·ªán:</span>
                  <span className="detail-value luxury">
                    {detectiveStoryCase.vehicle}
                  </span>
                </div>
                <div className="detail-row">
                  <span className="detail-label">Gi√° tr·ªã:</span>
