  // Typing animation effect
  useEffect(() => {
    if (currentStep >= storySteps.length) return;

    const fullText = storySteps[currentStep].text;
    let index = 0;
    setDisplayText("");
    setIsTyping(true);

    const typingInterval = setInterval(() => {
      if (index < fullText.length) {
