        .explanation-title {
          font-size: 13px;
          font-weight: 600;
          color: #9b59b6;
        }

        .attention-weights {
          display: flex;
          gap: 20px;
          margin-top: 15px;
        }

        .attention-item {
          flex: 1;
          text-align: center;
        }

        .attention-circle {
          width: 80px;
          height: 80px;
          margin: 0 auto 10px;
          position: relative;
        }

        .attention-circle svg {
          transform: rotate(-90deg);
        }

        .attention-bg {
          fill: none;
          stroke: #1a2530;
          stroke-width: 8;
        }

        .attention-fill {
          fill: none;
          stroke-width: 8;
          stroke-linecap: round;
          transition: stroke-dashoffset 1s ease;
        }

        .attention-fill.temporal { stroke: #f39c12; }
        .attention-fill.spatial { stroke: #3498db; }

        .attention-value {
          position: absolute;
          top: 50%;
          left: 50%;
          transform: translate(-50%, -50%);
          font-family: 'JetBrains Mono', monospace;
          font-size: 18px;
          font-weight: 600;
          color: #e0e6ed;
        }

        .attention-label {
          font-size: 11px;
          color: #6b7c93;
          text-transform: uppercase;
        }

        /* Footer */
        .footer {
          padding: 20px 40px;
          text-align: center;
          border-top: 1px solid rgba(42, 58, 74, 0.3);
          color: #4a5568;
          font-size: 12px;
        }

        /* Responsive */
        @media (max-width: 1200px) {
          .main-content {
            grid-template-columns: 1fr;
          }
        }
