import React, { useState, useEffect, useRef } from "react";

// Mock data for Chicago crime network
const mockCrimes = [
  {
    id: "CR-2024-001",
    type: "MOTOR VEHICLE THEFT",
    lat: 41.8781,
    lng: -87.6298,
    date: "2024-01-15",
    time: "02:30",
    district: 7,
    arrested: false,
  },
  {
    id: "CR-2024-002",
    type: "MOTOR VEHICLE THEFT",
    lat: 41.8812,
    lng: -87.6245,
    date: "2024-01-14",
    time: "03:15",
    district: 7,
    arrested: false,
  },
  {
    id: "CR-2024-003",
    type: "MOTOR VEHICLE THEFT",
    lat: 41.8756,
    lng: -87.6312,
    date: "2024-01-13",
    time: "02:45",
    district: 8,
    arrested: true,
  },
  {
    id: "CR-2024-004",
    type: "BURGLARY",
    lat: 41.8834,
    lng: -87.6289,
    date: "2024-01-15",
    time: "04:00",
    district: 7,
    arrested: false,
  },
  {
    id: "CR-2024-005",
    type: "NARCOTICS",
    lat: 41.8901,
    lng: -87.6456,
    date: "2024-01-16",
    time: "22:30",
    district: 11,
    arrested: true,
  },
];

const relatedCrimes = [
  {
    id: "CR-2024-002",
    similarity: 0.92,
    spatialWeight: 0.85,
    temporalWeight: 0.78,
    semanticWeight: 0.95,
  },
  {
    id: "CR-2024-003",
    similarity: 0.87,
    spatialWeight: 0.72,
    temporalWeight: 0.65,
    semanticWeight: 0.91,
  },
  {
    id: "CR-2024-004",
    similarity: 0.86,
    spatialWeight: 0.88,
    temporalWeight: 0.82,
    semanticWeight: 0.45,
  },
];

// Animated scanning effect component
const ScanningOverlay = ({ isScanning, progress }) => {
  if (!isScanning) return null;
  return (
    <div className="scanning-overlay">
      <div
        className="scan-ring"
        style={{
          transform: `scale(${1 + progress * 0.5})`,
          opacity: 1 - progress * 0.3,
        }}
      />
      <div
        className="scan-ring delay"
        style={{
          transform: `scale(${1 + progress * 0.3})`,
          opacity: 1 - progress * 0.5,
        }}
      />
      <div className="scan-text">
        {progress < 0.33 && "Analyzing Spatial proximity..."}
        {progress >= 0.33 &&
          progress < 0.66 &&
          "Analyzing Temporal patterns..."}
        {progress >= 0.66 && "Detected semantic match..."}
      </div>
    </div>
  );
};

// Network Graph Visualization
const NetworkGraph = ({ nodes, edges, highlightedNodes, onNodeClick }) => {
  const svgRef = useRef(null);
  const [positions, setPositions] = useState([]);

  useEffect(() => {
    // Generate node positions in a force-directed layout simulation
    const centerX = 300;
    const centerY = 200;
    const newPositions = nodes.map((node, i) => ({
      ...node,
      x:
        centerX +
        Math.cos((i / nodes.length) * Math.PI * 2) * (100 + Math.random() * 80),
      y:
        centerY +
        Math.sin((i / nodes.length) * Math.PI * 2) * (80 + Math.random() * 60),
    }));
    setPositions(newPositions);
  }, [nodes]);

  return (
    <svg ref={svgRef} viewBox="0 0 600 400" className="network-graph">
      <defs>
        <filter id="glow">
          <feGaussianBlur stdDeviation="3" result="coloredBlur" />
          <feMerge>
            <feMergeNode in="coloredBlur" />
            <feMergeNode in="SourceGraphic" />
          </feMerge>
        </filter>
        <linearGradient id="edgeGradient" x1="0%" y1="0%" x2="100%" y2="0%">
          <stop offset="0%" stopColor="#00f5d4" stopOpacity="0.2" />
          <stop offset="50%" stopColor="#00f5d4" stopOpacity="0.8" />
          <stop offset="100%" stopColor="#00f5d4" stopOpacity="0.2" />
        </linearGradient>
      </defs>

      {/* Edges */}
      {edges.map((edge, i) => {
        const source = positions.find((p) => p.id === edge.source);
        const target = positions.find((p) => p.id === edge.target);
        if (!source || !target) return null;
        const isHighlighted =
          highlightedNodes.includes(edge.source) &&
          highlightedNodes.includes(edge.target);
        return (
          <g key={i}>
            <line
              x1={source.x}
              y1={source.y}
              x2={target.x}
              y2={target.y}
              stroke={isHighlighted ? "#00f5d4" : "#2a3a4a"}
              strokeWidth={isHighlighted ? 2 : 1}
              opacity={isHighlighted ? 1 : 0.3}
              className={isHighlighted ? "edge-animated" : ""}
            />
            {isHighlighted && (
              <circle r="3" fill="#00f5d4" filter="url(#glow)">
                <animateMotion
                  dur="1.5s"
                  repeatCount="indefinite"
                  path={`M${source.x},${source.y} L${target.x},${target.y}`}
                />
              </circle>
            )}
          </g>
        );
      })}

      {/* Nodes */}
      {positions.map((node, i) => {
        const isHighlighted = highlightedNodes.includes(node.id);
        const nodeColor =
          node.type === "MOTOR VEHICLE THEFT"
            ? "#ff6b6b"
            : node.type === "NARCOTICS"
            ? "#9b59b6"
            : node.type === "BURGLARY"
            ? "#f39c12"
            : "#3498db";
        return (
          <g
            key={node.id}
            onClick={() => onNodeClick(node)}
            style={{ cursor: "pointer" }}
          >
            {isHighlighted && (
              <circle
                cx={node.x}
                cy={node.y}
                r="20"
                fill="none"
                stroke="#00f5d4"
                strokeWidth="2"
                opacity="0.5"
                className="pulse-ring"
              />
            )}
            <circle
              cx={node.x}
              cy={node.y}
              r={isHighlighted ? 12 : 8}
              fill={nodeColor}
              filter={isHighlighted ? "url(#glow)" : ""}
              className={isHighlighted ? "node-highlighted" : ""}
            />
            {isHighlighted && (
              <text
                x={node.x}
                y={node.y - 20}
                textAnchor="middle"
                fill="#fff"
                fontSize="10"
              >
                {node.id}
              </text>
            )}
          </g>
        );
      })}
    </svg>
  );
};

// Radar Chart for profiling
const RadarChart = ({ data, labels }) => {
  const centerX = 150;
  const centerY = 150;
  const radius = 100;
  const angleStep = (Math.PI * 2) / labels.length;

  const getPoint = (value, index) => {
    const angle = index * angleStep - Math.PI / 2;
    return {
      x: centerX + Math.cos(angle) * radius * value,
      y: centerY + Math.sin(angle) * radius * value,
    };
  };

  const cityAvgPath =
    data.cityAvg
      .map((v, i) => getPoint(v, i))
      .map((p, i) => `${i === 0 ? "M" : "L"}${p.x},${p.y}`)
      .join(" ") + "Z";
  const groupPath =
    data.group
      .map((v, i) => getPoint(v, i))
      .map((p, i) => `${i === 0 ? "M" : "L"}${p.x},${p.y}`)
      .join(" ") + "Z";

  return (
    <svg viewBox="0 0 300 300" className="radar-chart">
      {/* Grid circles */}
      {[0.25, 0.5, 0.75, 1].map((scale) => (
        <circle
          key={scale}
          cx={centerX}
          cy={centerY}
          r={radius * scale}
          fill="none"
          stroke="#2a3a4a"
          strokeWidth="1"
        />
      ))}

      {/* Grid lines */}
      {labels.map((_, i) => {
        const point = getPoint(1, i);
        return (
          <line
            key={i}
            x1={centerX}
            y1={centerY}
            x2={point.x}
            y2={point.y}
            stroke="#2a3a4a"
            strokeWidth="1"
          />
        );
      })}

      {/* City average */}
      <path
        d={cityAvgPath}
        fill="rgba(52, 152, 219, 0.2)"
        stroke="#3498db"
        strokeWidth="2"
      />

      {/* Group data */}
      <path
        d={groupPath}
        fill="rgba(0, 245, 212, 0.3)"
        stroke="#00f5d4"
        strokeWidth="2"
      />

      {/* Labels */}
      {labels.map((label, i) => {
        const point = getPoint(1.2, i);
        return (
          <text
            key={i}
            x={point.x}
            y={point.y}
            textAnchor="middle"
            fill="#8899aa"
            fontSize="10"
          >
            {label}
          </text>
        );
      })}
    </svg>
  );
};

