import React, { useState } from "react";
import Header from "./components/layout/Header";
import Navigation from "./components/layout/Navigation";
import Footer from "./components/layout/Footer";
import InvestigationTab from "./components/tabs/InvestigationTab";
import ForecastTab from "./components/tabs/ForecastTab";
import ProfilingTab from "./components/tabs/ProfilingTab";
import { mockCrimes, graphEdges } from "./data/mockData";
import "./styles/Dashboard.css";

export default function CrimeLinkDashboard() {
  // Tab state
  const [activeTab, setActiveTab] = useState("investigate");

  // Investigation tab states
  const [selectedCrime, setSelectedCrime] = useState(null);
  const [isScanning, setIsScanning] = useState(false);
  const [scanProgress, setScanProgress] = useState(0);
  const [showResults, setShowResults] = useState(false);
  const [highlightedNodes, setHighlightedNodes] = useState([]);

  // Forecast tab states
  const [timeSlider, setTimeSlider] = useState(0);
  const [timeFilter, setTimeFilter] = useState("weekend");
  const [crimeTypeFilter, setCrimeTypeFilter] = useState("narcotics");
  const [isForecasting, setIsForecasting] = useState(false);
  const [forecastComplete, setForecastComplete] = useState(false);

  // Profiling tab states
  const [selectedCluster, setSelectedCluster] = useState(null);
  const [showExplanation, setShowExplanation] = useState(false);

  // Graph data
  const graphNodes = mockCrimes.map((c) => ({
    id: c.id,
    type: c.type,
    lat: c.lat,
    lng: c.lng,
  }));

  // Simulate scanning process for investigation tab
  const handleScan = () => {
    if (!selectedCrime) return;
    setIsScanning(true);
    setScanProgress(0);
    setShowResults(false);
    setHighlightedNodes([selectedCrime.id]);

    const interval = setInterval(() => {
      setScanProgress((prev) => {
        if (prev >= 1) {
          clearInterval(interval);
          setIsScanning(false);
          setShowResults(true);
          setHighlightedNodes([
            "CR-2024-001",
            "CR-2024-002",
            "CR-2024-003",
            "CR-2024-004",
          ]);
          return 1;
        }
        return prev + 0.02;
      });
    }, 50);
  };

  return (
    <div className="dashboard">
      <Header />
      <Navigation activeTab={activeTab} setActiveTab={setActiveTab} />

      <main className="main-content">
        {activeTab === "investigate" && (
          <InvestigationTab
            selectedCrime={selectedCrime}
            setSelectedCrime={setSelectedCrime}
            isScanning={isScanning}
            scanProgress={scanProgress}
            showResults={showResults}
            highlightedNodes={highlightedNodes}
            handleScan={handleScan}
            graphNodes={graphNodes}
            graphEdges={graphEdges}
            setHighlightedNodes={setHighlightedNodes}
            setShowResults={setShowResults}
          />
        )}

        {activeTab === "forecast" && (
          <ForecastTab
            timeFilter={timeFilter}
            setTimeFilter={setTimeFilter}
            crimeTypeFilter={crimeTypeFilter}
            setCrimeTypeFilter={setCrimeTypeFilter}
            timeSlider={timeSlider}
            setTimeSlider={setTimeSlider}
            isForecasting={isForecasting}
            setIsForecasting={setIsForecasting}
            forecastComplete={forecastComplete}
            setForecastComplete={setForecastComplete}
          />
        )}

        {activeTab === "profile" && (
          <ProfilingTab
            selectedCluster={selectedCluster}
            setSelectedCluster={setSelectedCluster}
            showExplanation={showExplanation}
            setShowExplanation={setShowExplanation}
          />
        )}
      </main>

      <Footer />
    </div>
  );
}
